package leetCode;

public class Count {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int space=0;
		String s ="Hello arunjith";
		
		for(int i=0;i<=s.length()-1;i++) {
			
		char ch=s.charAt(i);
		if(ch==' ');
		space++;
		
			
		}
		System.out.println("Total white space : "+space);

	}

}
